mapper.insert:
		Insert into
		td_f_project
		(
		name,
		departId,
		level,
		amount,
		inputDate,
		releaseTime,
		orgId,
		townId,
		hamletId
		)
		values (
		#{name,jdbcType=VARCHAR},
		#{departId,jdbcType=VARCHAR},
		#{level,jdbcType=INTEGER}
		#{amount,jdbcType=DOUBLE}
		#{inputDate,jdbcType=DATE}
		#{releaseTime,jdbcType=DATE}
		#{orgId,jdbcType=VARCHAR}
		#{townId,jdbcType=VARCHAR}
		#{hamletId,jdbcType=VARCHAR}

		)

mapper.update:
updae td_f_project
set id=#{id}

	,name=#{name}


	,departId=#{departId}


	,level=#{level}


	,amount=#{amount}


	,inputDate=#{inputDate}


	,releaseTime=#{releaseTime}


	,orgId=#{orgId}


	,townId=#{townId}


	,hamletId=#{hamletId}

where id=#{id}

mapper.delete:
		delete from td_f_project
		where departId=#{departId}
		
			id=#{id}
